// --------------
// Exported Types
// --------------

export enum FusebitCharacterType {
  manOne = 'manOne',
  manTwo = 'manTwo',
  womanOne = 'womanOne',
}

export type FusebitCharacterProps = {
  size?: number;
  characterType?: FusebitCharacterType;
} & BoxProps;

// -------------------
// Exported Components
// -------------------

export function FusebitCharacter({ size, expand, characterType, ...rest }: FusebitCharacterProps) {
  size = !expand && !size ? 300 : size;
  const characterSize = size ? size * 1.5 : size;

  let character;
  switch (characterType) {
    case FusebitCharacterType.manTwo:
      character = manTwo(characterSize);
      break;
    case FusebitCharacterType.womanOne:
      character = womanOne(characterSize);
      break;
    default:
      character = manOne(characterSize);
  }
  return (
    <RelativeBox center middle expand={!size} width={size || '100%'} {...rest}>
      <AbsoluteBox width={size || '100%'}>{backgroundDetail()}</AbsoluteBox>
      {character}
    </RelativeBox>
  );
}
